import { useState } from "react";

const initialItems = [
    { id: 1, description: "Passports", quantity: 2, packed: false },
    { id: 2, description: "Socks", quantity: 12, packed: true },
    { id: 3, description: "Charger", quantity: 1, packed: false }
];

export default function App() {
    const [items, setItems] = useState([]);
    return <div className="app">
        <Logo />
        <Form />
        <PackingList />
        <Stats />
    </div>
}
function Logo() {
    return <h1>Far Away</h1>;
}
function Form() {
    const [description, setDescription] = useState("");
    const [quantity, setQuantity] = useState(1);


    function handleAddItems(item) {
        setItems((items) => [...items, item])
    }

    function handleSubmit(e) {
        //防止頁面刷新，這裡傳入的e是指event object
        e.preventDefault();
        if (!description) return;
        const newItem = { description, quantity, packed: false }
        setDescription("");
        setQuantity(1);
    }
    return (
        <form className="add-form" onSubmit={handleSubmit}>
            <h3>What do you need for your trip?</h3>
            <select value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (<option value={num} key={num}>{num}</option>))}
            </select>
            <input type="text" placeholder="Add an item" value={description} onChange={(e) => setDescription(e.target.value)} />
            <button >Add Item</button>
        </form>
    );
}

function PackingList() {
    return <ul className="LIST">
        {initialItems.map((items) => <Item item={items} key={items.id} />)}
    </ul>;
}

function Item({ item }) {
    return (
        <li >
            <span style={item.packed ? { textDecoration: "line-through" } : {}}>{item.quantity}{item.description}</span>
            <button>❌</button>
        </li >
    )
}

function Stats() {
    return (
        <footer className="stats">
            <em>You have x items on your list, and you already packed x items</em>
        </footer>
    )
}
