import { useState } from "react";

const initialItems = [
    { id: 1, description: "Passports", quantity: 2, packed: false },
    { id: 2, description: "Socks", quantity: 12, packed: true },
    { id: 3, description: "Charger", quantity: 1, packed: false }
];

export default function App() {
    const [items, setItems] = useState([]);
    function handleAddItems(item) {
        setItems((items) => [...items, item])
    }
    function deleteItems(id) {
        setItems((items) => items.filter(item => item.id !== id))
    }
    return <div className="app">
        <Logo />
        <Form onAddItmes={handleAddItems} />
        <PackingList items={items} onDeleteItem={deleteItems} />
        <Stats />
    </div>
}
function Logo() {
    return <h1>Far Away</h1>;
}
function Form({ onAddItmes }) {
    const [description, setDescription] = useState("");
    const [quantity, setQuantity] = useState(1);




    function handleSubmit(e) {
        //防止頁面刷新，這裡傳入的e是指event object
        e.preventDefault();
        if (!description) return;
        const newItem = { description, quantity, packed: false, id: Date.now() }
        console.log(newItem);
        onAddItmes(newItem);
        setDescription("");
        setQuantity(1);
    }
    return (
        <form className="add-form" onSubmit={handleSubmit}>
            <h3>What do you need for your trip?</h3>
            <select value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (<option value={num} key={num}>{num}</option>))}
            </select>
            <input type="text" placeholder="Add an item" value={description} onChange={(e) => setDescription(e.target.value)} />
            <button >Add Item</button>
        </form>
    );
}

function PackingList({ items, onDeleteItem }) {
    return (
        <ul className="LIST">
            {items.map((item) => <Item item={item} key={item.id} onDeleteItem={onDeleteItem} />)}
        </ul>
    )
}

function Item({ item, onDeleteItem }) {
    return (
        <li >
            <input type="checkbox" vlaue={item.checked} />
            <span style={item.packed ? { textDecoration: "line-through" } : {}}>{item.quantity}{item.description}</span>
            <button onClick={() => onDeleteItem(item.id)}>❌</button>
        </li >
    )
}

function Stats() {
    return (
        <footer className="stats">
            <em>You have x items on your list, and you already packed x items</em>
        </footer>
    )
}
